INSERT INTO `news` (`id`, `CateId`, `Title`, `Description`, `Content`, `KeyWord`, `Author`, `Picture`, `CreateDate`, `Link`, `Status`) VALUES
(153, 43, 'Huyền My chọn váy áo trước ngày thi Hoa hậu Hòa bình Quốc tế', 'Á hậu vừa có buổi thử trang phục dạ hội của nhà thiết kế Phạm Đăng Anh Thư và áo dài của Ngô Nhật Huy.', '<article class=\"content_detail fck_detail width_common block_ads_connect\"><div id=\"article_content\" class=\"fck_detail width_common block_ads_connect\">\n<div class=\"item_slide_show clearfix\">\n<div class=\"block_thumb_slide_show\">\n<!--[if lte IE 9]>\r\n                                            <img data-reference-id=\"25097249\" src=\"https://i-giaitri.vnecdn.net/2017/09/21/batch-IMG-8831-1506010723_680x0.jpg\" data-src=\"https://i-giaitri.vnecdn.net/2017/09/21/batch-IMG-8831-1506010723_680x0.jpg\" class=\"left\" data-component-caption=\"&amp;lt;p class=&amp;quot;Normal&amp;quot;&amp;gt;\n	Huyền My sẽ đến với cuộc thi Miss Grand International 2017 (Hoa hậu Hòa bình quốc tế) trong tháng tới. Á hậu Việt Nam 2016 chia sẻ cô đang rất tự tin và cố gắng hết sức ở sân chơi.&amp;lt;/p&amp;gt;\" />\r\n                                            <a href=\"javascript:;\" class=\"icon_thumb_videophoto icon_thumb_zoom btn_icon_show_slide_show\"><i class=\"ic ic-expand\"></i></a>\r\n                                            <![endif]-->\n<!--[if !IE]><!-->\n<img data-reference-id=\"25097249\" data-src=\"https://i-giaitri.vnecdn.net/2017/09/21/batch-IMG-8831-1506010723_680x0.jpg\" class=\"left\" data-component-caption=\"&amp;lt;p class=&amp;quot;Normal&amp;quot;&amp;gt;&#10;&#9;Huyền My sẽ đến với cuộc thi Miss Grand International 2017 (Hoa hậu Hòa bình quốc tế) trong tháng tới. Á hậu Việt Nam 2016 chia sẻ cô đang rất tự tin và cố gắng hết sức ở sân chơi.&amp;lt;/p&amp;gt;\"></img><a href=\"javascript:;\" class=\"icon_thumb_videophoto icon_thumb_zoom btn_icon_show_slide_show\"><i class=\"ic ic-expand\"></i></a>\n<!--<![endif]-->\n<noscript>\n<img data-reference-id=\"25097249\" src=\"https://i-giaitri.vnecdn.net/2017/09/21/batch-IMG-8831-1506010723_680x0.jpg\" class=\"left\" data-component-c